<?phpnamespace Nemundo\Meteo\MeteoschweizAllgemeineLage\Content\LatestAllgemeineLage;use Nemundo\Content\View\AbstractContentView;use Nemundo\Db\Sql\Order\SortOrder;use Nemundo\Html\Formatting\Small;use Nemundo\Meteo\MeteoschweizAllgemeineLage\Content\AllgemeineLage\AllgemeineLageContentType;use Nemundo\Meteo\MeteoschweizAllgemeineLage\Data\AllgemeineLage\AllgemeineLageReader;class LatestAllgemeineLageContentView extends AbstractContentView{    /**     * @var LatestAllgemeineLageContentType     */    public $contentType;    protected function loadView()    {        $this->viewName = 'default';        $this->viewId = '668a90c5-04a8-4f32-923f-442e42d2c3b0';        $this->defaultView = true;    }    public function getContent()    {        $reader = new AllgemeineLageReader();        $reader->addOrder($reader->model->id, SortOrder::DESCENDING);        $reader->limit = 1;        foreach ($reader->getData() as $allgemeineLageRow) {            (new AllgemeineLageContentType($allgemeineLageRow->id))->getDefaultView($this);        }        $small = new Small($this);        $small->content = 'Quelle MeteoSchweiz';        return parent::getContent();    }}