<?phpnamespace Nemundo\Meteo\MeteoschweizAllgemeineLage\Install;use Nemundo\App\Application\Setup\ApplicationSetup;use Nemundo\App\Scheduler\Setup\SchedulerSetup;use Nemundo\App\WebService\Setup\ServiceRequestSetup;use Nemundo\Content\App\Timeline\Application\TimelineApplication;use Nemundo\Content\Setup\ContentTypeSetup;use Nemundo\Meteo\MeteoschweizAllgemeineLage\Application\MeteoschweizAllgemeineLageApplication;use Nemundo\Meteo\MeteoschweizAllgemeineLage\Content\AllgemeineLage\AllgemeineLageContentType;use Nemundo\Meteo\MeteoschweizAllgemeineLage\Content\LatestAllgemeineLage\LatestAllgemeineLageContentType;use Nemundo\Meteo\MeteoschweizAllgemeineLage\Data\MeteoschweizAllgemeineLageModelCollection;use Nemundo\Meteo\MeteoschweizAllgemeineLage\Scheduler\AllgemeineLageScheduler;use Nemundo\Meteo\MeteoschweizAllgemeineLage\Service\AllgemeineLageService;use Nemundo\Model\Setup\ModelCollectionSetup;use Nemundo\App\Application\Type\Install\AbstractInstall;class MeteoschweizAllgemeineLageInstall extends AbstractInstall{    public function install()    {        (new TimelineApplication())->installApp();        (new ApplicationSetup())            ->addApplication(new MeteoschweizAllgemeineLageApplication());        (new ModelCollectionSetup())            ->addCollection(new MeteoschweizAllgemeineLageModelCollection());        (new ContentTypeSetup(new MeteoschweizAllgemeineLageApplication()))            ->addContentType(new AllgemeineLageContentType())            ->addContentType(new LatestAllgemeineLageContentType());        (new SchedulerSetup(new MeteoschweizAllgemeineLageApplication()))            ->addScheduler(new AllgemeineLageScheduler());        (new ServiceRequestSetup(new MeteoschweizAllgemeineLageApplication()))            ->addService(new AllgemeineLageService());    }}