<?phpnamespace Nemundo\Meteo\MeteoschweizAllgemeineLage\Scheduler;use Nemundo\App\Scheduler\Job\AbstractScheduler;use Nemundo\Core\Debug\Debug;use Nemundo\Meteo\MeteoschweizAllgemeineLage\Content\AllgemeineLage\AllgemeineLageContentType;use Nemundo\Meteo\MeteoschweizAllgemeineLage\Crawler\AllgemeineLageCrawler;class AllgemeineLageScheduler extends AbstractScheduler{    protected function loadScheduler()    {        $this->consoleScript = true;        $this->scriptName = 'allgemeinelage-import';        $this->active = true;        $this->minute = 30;    }    public function run()    {        $crawler = new AllgemeineLageCrawler();        $type = new AllgemeineLageContentType();        $type->datumText = $crawler->datumText;        $type->datum = $crawler->datum;        $type->allgemeineLage = $crawler->allgemeineLage;        $type->saveType();        //(new Debug())->write($crawler->frontenkarteUrl);    }}