<?phpnamespace Nemundo\Meteo\MeteoschweizBlog\Crawler;use Nemundo\Content\App\Feed\Content\Feed\FeedContentType;use Nemundo\Content\App\Feed\Content\Item\FeedItemContentType;use Nemundo\Content\App\Feed\WebCrawler\AbstractFeedWebCrawler;use Nemundo\Core\Type\DateTime\Date;use Nemundo\Core\Type\DateTime\DateTime;use Nemundo\Core\Type\Text\Text;use Nemundo\Crawler\WebCrawler\WebCrawler;class MeteoschweizBlogCrawler extends AbstractFeedWebCrawler{    public function getFeedItem()    {        $url = 'https://www.meteoschweiz.admin.ch/home/aktuell/meteoschweiz-blog/meteoschweiz-blog.html?topic=/content/meteoswiss/tags/topics/wetter';        $feedType = new FeedContentType();        $feedType->feedTitle = 'Meteoschweiz Blog';        $feedType->feedUrl = $url;        $feedType->saveType();        $feedId = $feedType->getDataId();        $crawler = new WebCrawler();        $crawler->url = $url;        $crawler->regularExpression = '<h4 class="h3"><a href="(.*?)">(.*?)<.*?<p class="standardteaser__meta standardteaser__meta--date">(.*?)<.*?<p>(.*?)<';        foreach ($crawler->getData() as $crawlerRow) {            $type = new FeedItemContentType();            $type->feedId = $feedId;            $type->url = $crawlerRow->getValue(0);            $type->title = (new Text($crawlerRow->getValue(1)))->decodeHtmlCharacter()->getValue();            $type->description = $crawlerRow->getValue(3);            $type->feedDateTime = (new DateTime())->fromGermanLongFormat($crawlerRow->getValue(2));            $type->saveType();        }    }}