<?phpnamespace Nemundo\Meteo\Sat24\Com\ListBox;use Nemundo\Meteo\Sat24\Data\Continent\ContinentReader;use Nemundo\Package\Bootstrap\FormElement\BootstrapListBox;class ContinentListBox extends BootstrapListBox{    public function getContent()    {        $this->label = 'Continent';        $reader = new ContinentReader();        $reader->addOrder($reader->model->continent);        foreach ($reader->getData() as $continentRow) {            $this->addItem($continentRow->id, $continentRow->continent);        }        return parent::getContent();    }}