<?phpnamespace Nemundo\Meteo\Sat24\Com\ListBox;use Nemundo\Meteo\Sat24\Data\ImageType\ImageTypeReader;use Nemundo\Package\Bootstrap\FormElement\BootstrapListBox;class ImageTypeListBox extends BootstrapListBox{    public function getContent()    {        $this->label = 'Image Type';        $reader=new ImageTypeReader();        foreach ($reader->getData() as $imageTypeRow) {            $this->addItem($imageTypeRow->id,$imageTypeRow->imageType);        }        return parent::getContent();    }}