<?phpnamespace Nemundo\Meteo\Sat24\Content\LatestSat24;use Nemundo\Content\Form\AbstractContentForm;use Nemundo\Meteo\Sat24\Com\ListBox\ContinentListBox;use Nemundo\Meteo\Sat24\Com\ListBox\ImageTypeListBox;class LatestSat24ContentForm extends AbstractContentForm{    /**     * @var LatestSat24ContentType     */    public $contentType;    /**     * @var ContinentListBox     */    private $continent;    /**     * @var ImageTypeListBox     */    private $imageType;    public function getContent()    {        $this->continent = new ContinentListBox($this);        $this->continent->validation = true;        $this->imageType = new ImageTypeListBox($this);        $this->imageType->validation = true;        return parent::getContent();    }    protected function loadUpdateForm()    {        $latestRow=$this->contentType->getDataRow();        $this->continent->value=$latestRow->continentId;        $this->imageType->value=$latestRow->imageTypeId;    }    public function onSubmit()    {        $this->contentType->continentId = $this->continent->getValue();        $this->contentType->imageTypeId = $this->imageType->getValue();        $this->contentType->saveType();    }}