<?phpnamespace Nemundo\Meteo\Sat24\Data\Continent;class ContinentPaginationReader extends \Nemundo\Model\Reader\AbstractPaginationModelDataReader {/*** @var ContinentModel*/public $model;public function __construct() {parent::__construct();$this->model = new ContinentModel();}/*** @return ContinentRow[]*/public function getData() {$list = [];foreach (parent::getData() as $dataRow) {$row = new ContinentRow($dataRow, $this->model);$list[] = $row;}return $list;}}