<?phpnamespace Nemundo\Meteo\Sat24\Data\Continent;class ContinentReader extends \Nemundo\Model\Reader\ModelDataReader {/*** @var ContinentModel*/public $model;public function __construct() {parent::__construct();$this->model = new ContinentModel();}/*** @return ContinentRow[]*/public function getData() {$list = [];foreach (parent::getData() as $dataRow) {$row = $this->getModelRow($dataRow);$list[] = $row;}return $list;}/*** @return ContinentRow*/public function getRow() {$dataRow = parent::getRow();$row = $this->getModelRow($dataRow);return $row;}/*** @return ContinentRow*/public function getRowById($id) {return parent::getRowById($id);}private function getModelRow($dataRow) {$row = new ContinentRow($dataRow, $this->model);$row->model = $this->model;return $row;}}