<?phpnamespace Nemundo\Meteo\Sat24\Data\LatestContent;class LatestContentReader extends \Nemundo\Model\Reader\ModelDataReader {/*** @var LatestContentModel*/public $model;public function __construct() {parent::__construct();$this->model = new LatestContentModel();}/*** @return LatestContentRow[]*/public function getData() {$list = [];foreach (parent::getData() as $dataRow) {$row = $this->getModelRow($dataRow);$list[] = $row;}return $list;}/*** @return LatestContentRow*/public function getRow() {$dataRow = parent::getRow();$row = $this->getModelRow($dataRow);return $row;}/*** @return LatestContentRow*/public function getRowById($id) {return parent::getRowById($id);}private function getModelRow($dataRow) {$row = new LatestContentRow($dataRow, $this->model);$row->model = $this->model;return $row;}}