<?phpnamespace Nemundo\Meteo\Zamg\Com;use Nemundo\Core\Type\DateTime\Date;use Nemundo\Meteo\Zamg\Download\WetterkarteImageUrl;use Nemundo\Package\Bootstrap\Image\BootstrapResponsiveImage;class WetterkarteRemoteImage extends BootstrapResponsiveImage{    /**     * @var Date     */    public $date;    public function getContent()    {        $imageUrl = new WetterkarteImageUrl();        $imageUrl->date = $this->date;        $this->src = $imageUrl->getImageUrl();        return parent::getContent();    }}