<?phpnamespace Nemundo\Meteo\Zamg\Data\Wetterkarte;class WetterkarteReader extends \Nemundo\Model\Reader\ModelDataReader {/*** @var WetterkarteModel*/public $model;public function __construct() {parent::__construct();$this->model = new WetterkarteModel();}/*** @return WetterkarteRow[]*/public function getData() {$list = [];foreach (parent::getData() as $dataRow) {$row = $this->getModelRow($dataRow);$list[] = $row;}return $list;}/*** @return WetterkarteRow*/public function getRow() {$dataRow = parent::getRow();$row = $this->getModelRow($dataRow);return $row;}/*** @return WetterkarteRow*/public function getRowById($id) {return parent::getRowById($id);}private function getModelRow($dataRow) {$row = new WetterkarteRow($dataRow, $this->model);$row->model = $this->model;return $row;}}