<?phpnamespace Nemundo\Meteo\Zamg\Data\Wetterkarte;class WetterkarteRow extends \Nemundo\Model\Row\AbstractModelDataRow {/*** @var \Nemundo\Model\Row\AbstractModelDataRow*/private $row;/*** @var WetterkarteModel*/public $model;/*** @var string*/public $id;/*** @var \Nemundo\Core\Type\DateTime\DateTime*/public $dateTime;/*** @var \Nemundo\Model\Reader\Property\File\ImageReaderProperty*/public $image;/*** @var \Nemundo\Core\Type\DateTime\Date*/public $date;public function __construct(\Nemundo\Db\Row\AbstractDataRow $row, $model) {parent::__construct($row->getData());$this->row = $row;$this->id = $this->getModelValue($model->id);$this->dateTime = new \Nemundo\Core\Type\DateTime\DateTime($this->getModelValue($model->dateTime));$this->image = new \Nemundo\Model\Reader\Property\File\ImageReaderProperty($row, $model->image);$value = $this->getModelValue($model->date);if ($value !== "0000-00-00") {$this->date = new \Nemundo\Core\Type\DateTime\Date($this->getModelValue($model->date));}}}