<?phpnamespace Nemundo\Meteo\Zamg\Data\WetterkarteJob;class WetterkarteJobReader extends \Nemundo\Model\Reader\ModelDataReader {/*** @var WetterkarteJobModel*/public $model;public function __construct() {parent::__construct();$this->model = new WetterkarteJobModel();}/*** @return WetterkarteJobRow[]*/public function getData() {$list = [];foreach (parent::getData() as $dataRow) {$row = $this->getModelRow($dataRow);$list[] = $row;}return $list;}/*** @return WetterkarteJobRow*/public function getRow() {$dataRow = parent::getRow();$row = $this->getModelRow($dataRow);return $row;}/*** @return WetterkarteJobRow*/public function getRowById($id) {return parent::getRowById($id);}private function getModelRow($dataRow) {$row = new WetterkarteJobRow($dataRow, $this->model);$row->model = $this->model;return $row;}}