<?phpnamespace Nemundo\Meteo\Zamg\Download;use Nemundo\Core\Base\AbstractBase;use Nemundo\Core\Type\DateTime\Date;class WetterkarteImageUrl extends AbstractBase{    /**     * @var Date     */    public $date;    public function __construct(Date $date=null)    {        $this->date=$date;    }    public function getTimeList()    {        // https://www.zamg.ac.at/fix/wetter/bodenkarte/2020/06/17/BK_BodAna_Sat_2006171200.png        // https://www.zamg.ac.at/fix/wetter/bodenkarte/2020/06/05/BK_BodAna_Sat_2006051800.png        //https://www.zamg.ac.at/fix/wetter/bodenkarte/2015/06/05/BK_BodAna_Sat_1506051800.png        $timeList = [];        $timeList[] = '00';        $timeList[] = '06';        $timeList[] = '12';        $timeList[] = '18';        return $timeList;    }    public function getImageUrl($hour = 12)    {        $url = 'https://www.zamg.ac.at/fix/wetter/bodenkarte/' .            $this->date->getYear() . '/' .            $this->date->getMonthNumberWithLeadingZero() . '/' .            $this->date->getDayWithLeadingZero() . '/BK_BodAna_Sat_' .            $this->date->getYear2Digit() .            $this->date->getMonthNumberWithLeadingZero() .            $this->date->getDayWithLeadingZero() .            $hour.'00.png';        return $url;    }    public function getImageUrlList() {    }}