<?phpnamespace Nemundo\Meteo\Zamg\Job;use Nemundo\Content\Form\AbstractContentForm;use Nemundo\Package\Bootstrap\FormElement\BootstrapFromToDatePicker;class WetterkarteImportForm extends AbstractContentForm{    /**     * @var WetterkarteImportJob     */    public $contentType;    /**     * @var BootstrapFromToDatePicker     */    private $dateFromTo;    public function getContent()    {        $this->dateFromTo=new BootstrapFromToDatePicker($this);        $this->dateFromTo->validation=true;        return parent::getContent();    }    protected function onSubmit()    {        $this->contentType->dateFrom=$this->dateFromTo->getDateFrom();        $this->contentType->dateTo=$this->dateFromTo->getDateTo();        $this->contentType->saveType();    }}