<?phpnamespace Nemundo\Meteo\Zamg\Reader;use Nemundo\Core\Base\DataSource\AbstractDataSource;use Nemundo\Core\Date\DayReader;use Nemundo\Core\Type\DateTime\Date;use Nemundo\Core\Type\DateTime\DateTime;use Nemundo\Meteo\Zamg\Download\WetterkarteImageUrl;//WebReaderclass ZamgWetterkarteReader extends AbstractDataSource{    /**     * @var Date     */    public $dateFrom;    /**     * @var Date     */    public $dateTo;    protected function loadData()    {        $dateReader = new DayReader();        $dateReader->dateFrom = $this->dateFrom;        $dateReader->dateTo = $this->dateTo;        foreach ($dateReader->getData() as $date) {            //(new Debug())->write($date->getIsoDateFormat());            foreach ((new WetterkarteImageUrl())->getTimeList() as $hour) {                $dateTime = (new DateTime())->fromDate($date);                $dateTime->setHour($hour);                $item = new WetterkarteItem();                //$item->date = $date;                $item->dateTime = $dateTime;                $item->imageUrl = (new WetterkarteImageUrl($date))->getImageUrl($hour);                $this->addItem($item);            }        }    }    /**     * @return WetterkarteItem[]     */    public function getData()    {        return parent::getData(); // TODO: Change the autogenerated stub    }}