<?phpnamespace Nemundo\Meteo\Zamg\Script;use Nemundo\App\Script\Type\AbstractConsoleScript;use Nemundo\Core\Console\ConsoleInput;use Nemundo\Core\Type\DateTime\Date;use Nemundo\Meteo\Zamg\Import\WetterkarteImport;class WetterkarteImportScript extends AbstractConsoleScript{    protected function loadScript()    {        $this->scriptName = 'zamg-import';    }    public function run()    {        $input = new ConsoleInput();        $input->message = 'Date From';        $input->defaultValue = '2020-01-01';        $dateFrom = (new Date($input->getValue()));        $input = new ConsoleInput();        $input->message = 'Date To';        $input->defaultValue = '2020-01-01';        $dateTo = (new Date($input->getValue()));        $import = new WetterkarteImport();        $import->dateFrom = $dateFrom;        $import->dateTo = $dateTo;        $import->importWetterkarte();    }}