<?phpnamespace Nemundo\Meteo\Zamg\Content\ZamgWetterkarte;use Nemundo\Content\Form\AbstractContentForm;use Nemundo\Package\Bootstrap\FormElement\BootstrapDatePicker;class ZamgWetterkarteContentForm extends AbstractContentForm{    /**     * @var ZamgWetterkarteContentType     */    public $contentType;    /**     * @var BootstrapDatePicker     */    private $date;    public function getContent()    {        $this->date = new BootstrapDatePicker($this);        $this->date->label = 'Date';        return parent::getContent();    }    protected function loadUpdateForm()    {        $this->date->value = $this->contentType->getDataRow()->date->getShortDateLeadingZeroFormat();    }    public function onSubmit()    {        $this->contentType->date = $this->date->getDateValue();        $this->contentType->saveType();    }}