<?phpnamespace Nemundo\Meteoschweiz\Com\Chart\Messwert;use Nemundo\Com\Chart\Data\LineChartData;use Nemundo\Meteoschweiz\Data\Messwert\MesswertRow;class SunshineChart extends AbstractMesswertChart{    /**     * @var LineChartData     */    private  $temperatureLine;    public function getContent()    {        $this->chartTitle = 'Sunshine';        $this->temperatureLine = new LineChartData($this);        $this->temperatureLine->legend = 'Sunshine';        return parent::getContent();    }    protected function onDataRow(MesswertRow $messwertRow)    {        if ($messwertRow->hasSunshine) {            $this->temperatureLine->addValue($messwertRow->sunshine);            //$this->addXAxisLabel($messwertRow->dateTime->dateTime->getShortDateTimeLeadingZeroFormat());        }    }}