<?phpnamespace Nemundo\Meteoschweiz\Com\Chart\Messwert;use Nemundo\Com\Chart\Data\LineChartData;use Nemundo\Meteoschweiz\Data\Messwert\MesswertRow;class TemperatureChart extends AbstractMesswertChart{    /**     * @var LineChartData     */    private  $temperatureLine;    /**     * @var LineChartData     */    private $dewPointLine;    public function getContent()    {        $this->chartTitle = 'Temperature/Dew Point';        $this->temperatureLine = new LineChartData($this);        $this->temperatureLine->legend = 'Temperature';        $this->dewPointLine = new LineChartData($this);        $this->dewPointLine->legend = 'Dew Point';        /*        foreach ($this->getMesswertReader() as $messwertRow) {            if ($messwertRow->hasTemperature) {                $temperatureLine->addValue($messwertRow->temperature);                $dewPointLine->addValue($messwertRow->dewPoint);                $this->addXAxisLabel($messwertRow->dateTime->dateTime->getShortDateTimeLeadingZeroFormat());            }        }*/        return parent::getContent();    }    protected function onDataRow(MesswertRow $messwertRow)    {        if ($messwertRow->hasTemperature) {            $this->temperatureLine->addValue($messwertRow->temperature);            $this->dewPointLine->addValue($messwertRow->dewPoint);            //$this->addXAxisLabel($messwertRow->dateTime->dateTime->getShortDateTimeLeadingZeroFormat());        }    }}