<?phpnamespace Nemundo\Meteoschweiz\Com\Form;use Nemundo\Com\FormBuilder\SearchForm;use Nemundo\Meteoschweiz\Com\ListBox\StationListBox;use Nemundo\Package\Bootstrap\Layout\Grid\BootstrapRow;class MesswertSearchForm extends SearchForm{    public function getContent()    {        $formRow = new BootstrapRow($this);        $station = new StationListBox($formRow);        $station->submitOnChange = true;        $station->searchMode = true;        $station->column = true;        $station->columnSize = 2;        return parent::getContent();    }}