<?phpnamespace Nemundo\Meteoschweiz\Com\ListBox;use Nemundo\Meteoschweiz\Content\Station\StationContentType;use Nemundo\Meteoschweiz\Data\MesswertStation\MesswertStationReader;use Nemundo\Meteoschweiz\Parameter\StationParameter;use Nemundo\Package\Bootstrap\FormElement\BootstrapListBox;class StationListBox extends BootstrapListBox{    protected function loadContainer()    {        parent::loadContainer();        $this->label = 'Station';        $this->name=(new StationParameter())->getParameterName();    }    public function getContent()    {        $reader = new MesswertStationReader();        $reader->addOrder($reader->model->station);        foreach ($reader->getData() as $stationRow) {            $this->addItem($stationRow->id, $stationRow->station);        }        return parent::getContent();    }    public function getStationContentType() {           return (new StationContentType($this->getValue()));    }}