<?phpnamespace Nemundo\Meteoschweiz\Content\Station;use Nemundo\Admin\Com\Table\AdminClickableTable;use Nemundo\Admin\Com\Table\AdminBootstrapTable;use Nemundo\Com\TableBuilder\TableHeader;use Nemundo\Com\TableBuilder\TableRow;use Nemundo\Content\Parameter\DataIdParameter;use Nemundo\Content\View\AbstractContentListing;use Nemundo\Geo\Map\GoogleMaps\GoogleMapsHyperlink;use Nemundo\Meteoschweiz\Data\MesswertStation\MesswertStationReader;use Nemundo\Package\Bootstrap\Table\BootstrapClickableTableRow;class StationContentListing extends AbstractContentListing{    public function getContent()    {        $reader=new MesswertStationReader();        $table=new AdminClickableTable($this);        $header=new TableHeader($table);        $header->addText($reader->model->station->label);        $header->addText($reader->model->stationCode->label);        $header->addText($reader->model->geoCoordinate->altitude->label);        foreach ($reader->getData() as $stationRow) {            $row=new BootstrapClickableTableRow($table);            $row->addText($stationRow->station);            $row->addText($stationRow->stationCode);            $row->addText($stationRow->geoCoordinate->altitude);            $site = $this->getRedirectSite($stationRow->id);            //$site->addParameter(new DataIdParameter($stationRow->id));            $row->addClickableSite($site);            /*$row->addText($stationRow->geoCoordinate->getText());            $hyperlink=new GoogleMapsHyperlink($row);            $hyperlink->geoCoordinate = $stationRow->geoCoordinate;*/        }        return parent::getContent();    }}