<?phpnamespace Nemundo\Meteoschweiz\Content\Station\View;use Nemundo\Core\Type\DateTime\DateTime;use Nemundo\Meteoschweiz\Com\Chart\Messwert\WindChart;class WindChartContentView extends AbstractStationContentView{    protected function loadView()    {        $this->viewName = 'Wind Chart';        $this->viewId = '11328474-2aa2-4f72-bc53-05e3025bc64a';    }    public function getContent()    {        $stationRow = $this->contentType->getDataRow();        $dateFrom = (new DateTime())->setNow()->minusDay(3);        $dateTo = (new DateTime())->setNow();        $chart = new WindChart($this);        $chart->stationId = $stationRow->id;        $chart->dateFrom = $dateFrom;        $chart->dateTo = $dateTo;        return parent::getContent();    }}