<?phpnamespace Nemundo\Meteoschweiz\Content\StationDifference\Form;use Nemundo\Content\Form\AbstractContentForm;use Nemundo\Meteoschweiz\Com\ListBox\StationListBox;use Nemundo\Meteoschweiz\Content\TemperatureDifference\AbstractStationDifferenceContentType;class StationDifferenceContentForm extends AbstractContentForm{    /**     * @var AbstractStationDifferenceContentType     */    public $contentType;    /**     * @var StationListBox     */    private $station1;    /**     * @var StationListBox     */    private $station2;    public function getContent()    {        $this->station1 = new StationListBox($this);        $this->station1->label = 'Station 1';        $this->station1->validation=true;        $this->station2 = new StationListBox($this);        $this->station2->label = 'Station 2';        $this->station2->validation=true;        return parent::getContent();    }    protected function loadUpdateForm()    {        $dataRow=$this->contentType->getDataRow();        $this->station1->value=$dataRow->station1Id;        $this->station2->value=$dataRow->station2Id;    }    public function onSubmit()    {        $this->contentType->station1Id=$this->station1->getValue();        $this->contentType->station2Id=$this->station2->getValue();        $this->contentType->saveType();    }}