<?phpnamespace Nemundo\Meteoschweiz\Content\StationDifference;use Nemundo\Meteoschweiz\Content\StationDifference\View\QnhDifferenceContentView;class QnhDifferenceContentType extends AbstractStationDifferenceContentType{    protected function loadContentType()    {        parent::loadContentType();        $this->typeLabel = 'QNH Difference';        $this->typeId = '52b038ab-3be4-491c-b566-288b38108412';        $this->viewClassList[] = QnhDifferenceContentView::class;    }    public function getSubject()    {        $differenceRow = $this->getDataRow();        $subject = $differenceRow->station1->station . ' - ' . $differenceRow->station2->station;        return $subject;    }}