<?phpnamespace Nemundo\Meteoschweiz\Content\TopListing;use Nemundo\Content\Form\AbstractContentForm;class TopListingContentForm extends AbstractContentForm{    /**     * @var TopListingContentType     */    public $contentType;    public function getContent()    {        return parent::getContent();    }    public function onSubmit()    {        $this->contentType->listingLimit = 10;        $this->contentType->saveType();    }}