<?phpnamespace Nemundo\Meteoschweiz\Content\TopListing;use Nemundo\Content\Type\AbstractContentType;use Nemundo\Meteoschweiz\Content\TopListing\View\TemperatureTopListingContentView;use Nemundo\Meteoschweiz\Content\TopListing\View\TopListingContentView;use Nemundo\Meteoschweiz\Content\TopListing\View\WindTopListingContentView;use Nemundo\Meteoschweiz\Data\TopListing\TopListing;use Nemundo\Meteoschweiz\Data\TopListing\TopListingReader;use Nemundo\Meteoschweiz\Data\TopListing\TopListingRow;class TopListingContentType extends AbstractContentType{    public $listingLimit;    protected function loadContentType()    {        $this->typeLabel = 'TopListing';        $this->typeId = '7a4114ba-1149-42ed-9ed9-f2d73b355112';        $this->formClassList[] = TopListingContentForm::class;        $this->viewClassList[] = TopListingContentView::class;        $this->viewClassList[] = WindTopListingContentView::class;        $this->viewClassList[] = TemperatureTopListingContentView::class;    }    protected function onCreate()    {        $data=new TopListing();        $data->listingLimit = $this->listingLimit;        $this->dataId = $data->save();    }    protected function onUpdate()    {    }    protected function onDelete()    {    }    protected function onIndex()    {    }    protected function onDataRow()    {        $this->dataRow=(new TopListingReader())->getRowById($this->getDataId());    }    /**     * @return \Nemundo\Model\Row\AbstractModelDataRow|TopListingRow     */    public function getDataRow()    {        return parent::getDataRow();    }}