<?phpnamespace Nemundo\Meteoschweiz\Data\CmsStation;class CmsStationPaginationReader extends \Nemundo\Model\Reader\AbstractPaginationModelDataReader {/*** @var CmsStationModel*/public $model;public function __construct() {parent::__construct();$this->model = new CmsStationModel();}/*** @return CmsStationRow[]*/public function getData() {$list = [];foreach (parent::getData() as $dataRow) {$row = new CmsStationRow($dataRow, $this->model);$list[] = $row;}return $list;}}