<?phpnamespace Nemundo\Meteoschweiz\Data\CmsStation;class CmsStationReader extends \Nemundo\Model\Reader\ModelDataReader {/*** @var CmsStationModel*/public $model;public function __construct() {parent::__construct();$this->model = new CmsStationModel();}/*** @return CmsStationRow[]*/public function getData() {$list = [];foreach (parent::getData() as $dataRow) {$row = $this->getModelRow($dataRow);$list[] = $row;}return $list;}/*** @return CmsStationRow*/public function getRow() {$dataRow = parent::getRow();$row = $this->getModelRow($dataRow);return $row;}/*** @return CmsStationRow*/public function getRowById($id) {return parent::getRowById($id);}private function getModelRow($dataRow) {$row = new CmsStationRow($dataRow, $this->model);$row->model = $this->model;return $row;}}