<?phpnamespace Nemundo\Meteoschweiz\Difference;use Nemundo\Db\Sql\Order\SortOrder;use Nemundo\Meteoschweiz\Data\Messwert\MesswertReader;class QnhDifference extends AbstractDifference{    public function getDifference()    {        $difference =round( $this->getValue($this->station1Id)-$this->getValue($this->station2Id),1);        return $difference;    }    // dateTime    private function getValue($stationId) {        //$stationCode = 'KLO';        $value = 0;        $reader = new MesswertReader();        //$reader->model->loadStation();        $reader->filter->andEqual($reader->model->stationId, $stationId);        $reader->addOrder($reader->model->id, SortOrder::DESCENDING);        $reader->limit = 1;        foreach ($reader->getData() as $messwertRow) {            $value = $messwertRow->qnh;            $this->dateTime = $messwertRow->dateTime;        }        // check datetime station1 und station2        return $value;    }}