<?phpnamespace Nemundo\Meteoschweiz\Import;use Nemundo\Core\Base\AbstractBase;use Nemundo\Meteoschweiz\Data\Messwert\Messwert;use Nemundo\Meteoschweiz\Data\MesswertDateTime\MesswertDateTime;use Nemundo\Meteoschweiz\Data\MesswertDateTime\MesswertDateTimeId;use Nemundo\Meteoschweiz\Data\MesswertStation\MesswertStation;use Nemundo\Meteoschweiz\Data\MesswertStation\MesswertStationId;use Nemundo\Meteoschweiz\Reader\Messwert\MesswertCsvReader;class MesswertImport extends AbstractBase{    public function importData()    {        $reader = new MesswertCsvReader();        foreach ($reader->getData() as $messwertItem) {            $data = new MesswertDateTime();            $data->ignoreIfExists = true;            $data->dateTime = $messwertItem->dateTime;            $data->date=$messwertItem->dateTime->getDate();            $data->save();            $id = new MesswertDateTimeId();            $id->filter->andEqual($id->model->dateTime, $messwertItem->dateTime->getIsoDateTime());            $dateTimeId = $id->getId();            $data = new MesswertStation();            $data->ignoreIfExists = true;            $data->stationCode = $messwertItem->stationCode;            $data->save();            $id = new MesswertStationId();            $id->filter->andEqual($id->model->stationCode, $messwertItem->stationCode);            $stationId = $id->getId();            $data = new Messwert();            $data->ignoreIfExists = true;            $data->stationId = $stationId;            $data->dateTimeId = $dateTimeId;            $data->hasTemperature = $messwertItem->hasTemperature;            $data->temperature = $messwertItem->temperature;            $data->dewPoint = $messwertItem->dewPoint;            $data->hasHumidity = $messwertItem->hasHumidity;            $data->humidity = $messwertItem->humidity;            $data->hasWind = $messwertItem->hasWind;            $data->wind = $messwertItem->wind;            $data->windGust = $messwertItem->windGust;            $data->windDirection = $messwertItem->windDirection;            $data->hasQnh = $messwertItem->hasQnh;            $data->qnh = $messwertItem->qnh;            $data->qfe = $messwertItem->qfe;            $data->hasQff = $messwertItem->hasQff;            $data->qff = $messwertItem->qff;            $data->hasPrecipitation = $messwertItem->hasPrecipitation;            $data->precipitation = $messwertItem->precipitation;            $data->hasSunshine = $messwertItem->hasSunshine;            $data->sunshine = $messwertItem->sunshine;            $data->globalRadiation = $messwertItem->globalRadiation;            $data->save();        }    }}