<?phpnamespace Nemundo\Meteoschweiz\Install;use Nemundo\App\Application\Setup\ApplicationSetup;use Nemundo\App\Application\Type\Install\AbstractInstall;use Nemundo\App\Scheduler\Setup\SchedulerSetup;use Nemundo\App\WebService\Setup\ServiceRequestSetup;use Nemundo\Content\Application\ContentApplication;use Nemundo\Content\Index\Geo\Application\GeoIndexApplication;use Nemundo\Content\Index\Geo\Setup\GeoContentTypeSetup;use Nemundo\Content\Setup\ContentTypeSetup;use Nemundo\Meteoschweiz\Application\MeteoschweizApplication;use Nemundo\Meteoschweiz\Content\MeteoschweizContentTypeCollection;use Nemundo\Meteoschweiz\Content\Station\StationContentType;use Nemundo\Meteoschweiz\Data\MeteoschweizModelCollection;use Nemundo\Meteoschweiz\Scheduler\MesswertScheduler;use Nemundo\Meteoschweiz\Service\MesswertLatestService;use Nemundo\Meteoschweiz\Service\MesswertService;use Nemundo\Meteoschweiz\Service\StationItemService;use Nemundo\Meteoschweiz\Service\StationSearchService;use Nemundo\Meteoschweiz\Service\StationWordService;use Nemundo\Meteoschweiz\Widget\Install\WidgetInstall;use Nemundo\Model\Setup\ModelCollectionSetup;class MeteoschweizInstall extends AbstractInstall{    public function install()    {        (new ContentApplication())->installApp();        //(new GeoIndexApplication())->installApp();        /*(new ApplicationSetup())            ->addApplication(new MeteoschweizApplication());*/        (new ModelCollectionSetup())            ->addCollection(new MeteoschweizModelCollection());        (new SchedulerSetup(new MeteoschweizApplication()))            ->addScheduler(new MesswertScheduler());        (new ContentTypeSetup(new MeteoschweizApplication()))            ->addContentTypeCollection(new MeteoschweizContentTypeCollection());        (new GeoContentTypeSetup(new MeteoschweizApplication()))            ->addContentType(new StationContentType());        //(new WidgetInstall())->install();        (new ServiceRequestSetup(new MeteoschweizApplication()))            ->addService(new StationItemService())            ->addService(new MesswertService())            ->addService(new MesswertLatestService())            ->addService(new StationWordService())            ->addService(new StationSearchService());        /*            ->addContentType(new StationContentType())            ->addContentType(new TopListingContentType())            //->addContentType(new WindChartContentType())            ->addContentType(new TemperatureDifferenceContentType());*/    }}