<?phpnamespace Nemundo\Meteoschweiz;use Nemundo\Content\ContentProject;use Nemundo\Project\AbstractProject;class MeteoschweizProject extends AbstractProject{    public function loadProject()    {        $this->project = 'Meteoschweiz';        $this->projectName = 'meteoschweiz';        $this->path = __DIR__;        $this->namespace = __NAMESPACE__;        $this->modelPath = __DIR__ . DIRECTORY_SEPARATOR . '..' . DIRECTORY_SEPARATOR . 'model' . DIRECTORY_SEPARATOR;        $this->addDependency(new ContentProject());    }}