<?phpnamespace Nemundo\Meteoschweiz\Page;use Nemundo\Admin\Com\Table\AdminBootstrapTable;use Nemundo\Com\TableBuilder\TableHeader;use Nemundo\Com\TableBuilder\TableRow;use Nemundo\Geo\Map\GoogleMaps\GoogleMapsHyperlink;use Nemundo\Meteoschweiz\Data\MesswertStation\MesswertStationReader;use Nemundo\Meteoschweiz\Template\MeteoschweizTemplate;use Nemundo\Package\Bootstrap\FormElement\BootstrapListBox;class StationPage extends MeteoschweizTemplate {    public function getContent()    {        $reader=new MesswertStationReader();        $table=new AdminBootstrapTable($this);        $header=new TableHeader($table);        $header->addText($reader->model->station->label);        $header->addText($reader->model->stationCode->label);        foreach ($reader->getData() as $stationRow) {            $row=new TableRow($table);            $row->addText($stationRow->station);            $row->addText($stationRow->stationCode);            $row->addText($stationRow->geoCoordinate->altitude);            $row->addText($stationRow->geoCoordinate->getText());            $hyperlink=new GoogleMapsHyperlink($row);            $hyperlink->geoCoordinate = $stationRow->geoCoordinate;        }        return parent::getContent();    }}