<?phpnamespace Nemundo\Meteoschweiz\Reader\Station;use Nemundo\Core\Base\DataSource\AbstractDataSource;use Nemundo\Core\Csv\Reader\FixWidthCsvReader;use Nemundo\Meteoschweiz\Download\StationDownload;use Nemundo\Meteoschweiz\Path\StationTmpPath;class StationJsonReader extends AbstractDataSource{    protected function loadData()    {        (new StationDownload())->downloadFile();        $csvReader = new FixWidthCsvReader();        $csvReader->filename = (new StationTmpPath())->getFilename();        $csvReader->lineOfStart = 18;        $csvReader->utf8Encode = true;        //$csvReader->addSeperationPosition(0);        $csvReader->addSeperationPosition(42);        $csvReader->addSeperationPosition(69);        $csvReader->addSeperationPosition(111);        $csvReader->addSeperationPosition(155);        $endOfList = false;        foreach ($csvReader->getData() as $csvRow) {            $stationCode = $csvRow[0];            if (!$endOfList) {                if ($stationCode === '') {                    $endOfList = true;                }            }            if (!$endOfList) {                $item = new StationItem();                $item->stationCode = $stationCode;                $item->station = $csvRow[1];                $item->globalCoordinate = $csvRow[2];                $item->swissCoordinate = $csvRow[3];                $item->elevation = $csvRow[4];                $this->addItem($item);            }        }    }    /**     * @return StationItem[]     */    public function getData()    {        return parent::getData();    }}