<?phpnamespace Nemundo\Meteoschweiz\Row;// Nemundo\Meteoschweiz\Row\MesswertCustomRowuse Nemundo\Core\Type\Number\Number;use Nemundo\Meteoschweiz\Data\Messwert\MesswertRow;class MesswertCustomRow extends MesswertRow{    public function getTemperatureText()    {        $text = '';        if ($this->hasTemperature) {            $temperature = new Number($this->temperature);            $text = $temperature->getFormatNumberAfterDecimal(1) . '° C';        }        return $text;    }    public function getDewPointText()    {        $text = '';        if ($this->hasTemperature) {            $temperature = new Number($this->dewPoint);            $temperature->getFormatNumber(1);            $temperature->getRoundedNumber(1);            $text = $temperature->getFormatNumberAfterDecimal() . ' ° C';        }        return $text;    }    public function getHumidityText()    {        $text = '';        if ($this->hasHumidity) {            $text = $this->humidity . ' %';        }        return $text;    }    public function getWindText()    {        $wind = '';        if ($this->hasWind) {            $wind = $this->wind . ' km/h | ' . $this->windGust . ' km/h | ' . $this->windDirection . ' Grad';        }        return $wind;    }    public function getPressureText()    {        $text = '';        if ($this->hasQnh) {            $text = 'QFE ' . $this->qfe . ' hPa | ';            if ($this->hasQff) {                $text .= 'QFF ' . $this->qff . ' hPa | ';            }            $text .= 'QNH ' . $this->qnh . ' hPa | ';        }        return $text;    }    public function getPrecipitationText()    {        $text = '';        if ($this->hasPrecipitation) {            $text = $this->precipitation . ' mm';        }        return $text;    }    public function getSunshineText()    {        $text = '';        if ($this->hasSunshine) {            //$sunshine = $this->sunshine . ' min / ' . $this->globalRadiation . ' W';            $text = $this->sunshine . ' min';        }        return $text;    }    public function getGlobalRadiationText()    {        $text = '';        if ($this->hasSunshine) {            $text = $this->globalRadiation . ' W';        }        return $text;    }}