<?phpnamespace Nemundo\Meteoschweiz\Scheduler;use Nemundo\App\Scheduler\Job\AbstractScheduler;use Nemundo\Meteoschweiz\Import\MesswertImport;use Nemundo\Meteoschweiz\Import\StationImport;use Nemundo\Meteoschweiz\Path\MeteoschweizTmpPath;use Nemundo\Meteoschweiz\Script\MeteoschweizCleanScript;class MesswertScheduler extends AbstractScheduler{    protected function loadScheduler()    {        $this->active = false; // true;        $this->minute = 5;        $this->consoleScript = true;        $this->scriptName = 'meteoschweiz-messwert';    }    public function run()    {        (new MeteoschweizTmpPath())->createPath();        (new MesswertImport())->importData();        (new StationImport())->importStation();    }}