<?phpnamespace Nemundo\Meteoschweiz\Site;use Nemundo\Geo\Kml\Document\KmlDocument;use Nemundo\Geo\Kml\Object\KmlMarker;use Nemundo\Meteoschweiz\Data\MesswertStation\MesswertStationReader;use Nemundo\Web\Site\AbstractSite;class MesswertStationKmlSite extends AbstractSite{    /**     * @var MesswertStationKmlSite     */    public static $site;    protected function loadSite()    {        $this->title = 'Messwert Station Kml';        $this->url = 'messwert-station-kml';        MesswertStationKmlSite::$site = $this;    }    public function loadContent()    {        $kml = new KmlDocument();        $reader = new MesswertStationReader();        foreach ($reader->getData() as $stationRow) {            $point = new KmlMarker($kml);            $point->label = $stationRow->station;            $point->coordinate = $stationRow->geoCoordinate;        }        $kml->render();    }}