<?phpnamespace Nemundo\Meteoschweiz\Site;use Nemundo\Meteoschweiz\Page\StationItemPage;use Nemundo\Web\Site\AbstractSite;class StationItemSite extends AbstractSite{    /**     * @var StationItemSite     */    public static $site;    protected function loadSite()    {        $this->title = 'Station Item';        $this->url = 'station-item';        $this->menuActive=false;        StationItemSite::$site=$this;    }    public function loadContent()    {        (new StationItemPage())->render();    }}