<?phpnamespace Nemundo\Meteoschweiz\Widget;use Nemundo\Admin\Com\Widget\AdminWidget;use Nemundo\Html\Paragraph\Paragraph;use Nemundo\Meteoschweiz\Com\Chart\PressureDifferenceChart;use Nemundo\Meteoschweiz\Com\Small\MeteoschweizSourceSmall;use Nemundo\Meteoschweiz\Data\MesswertStation\MesswertStationId;use Nemundo\Meteoschweiz\Difference\QnhDifference;class FoehnWidget extends AdminWidget  // PressureDifferenceWidget{    public function getContent()    {        $this->widgetTitle = 'Föhn (Kloten-Lugano)';        $stationCode1 = 'KLO';        $stationCode2 = 'LUG';        $id = new MesswertStationId();        $id->filter->andEqual($id->model->stationCode, $stationCode1);        $stationId1 = $id->getId();        $id = new MesswertStationId();        $id->filter->andEqual($id->model->stationCode, $stationCode2);        $stationId2 = $id->getId();        $difference = new QnhDifference();        $difference->station1Id = $stationId1;        $difference->station2Id = $stationId2;        $pressureDifference = $difference->getDifference();        $text = $pressureDifference . ' hPa';        if ($pressureDifference>0) {            $text.=' (Nordüberdruck)';        }        if ($pressureDifference<0) {            $text.=' (Südüberdruck)';        }        $p=new Paragraph($this);        $p->content = $text;        $chart=new PressureDifferenceChart($this);        $chart->stationCode1 = $stationCode1;        $chart->stationCode2 = $stationCode2;        new MeteoschweizSourceSmall($this);        return parent::getContent();    }}