<?phpnamespace Nemundo\Meteoschweiz\Widget\Install;use Nemundo\App\Application\Setup\ApplicationSetup;use Nemundo\App\Application\Type\Install\AbstractInstall;use Nemundo\Content\Setup\ContentTypeSetup;use Nemundo\Meteoschweiz\Application\MeteoschweizApplication;use Nemundo\Meteoschweiz\Widget\Application\WidgetApplication;use Nemundo\Meteoschweiz\Widget\Content\MaxTemperatureToday\MaxTemperatureTodayContentType;use Nemundo\Meteoschweiz\Widget\Content\StationSearch\StationSearchContentType;use Nemundo\Meteoschweiz\Widget\Data\WidgetModelCollection;use Nemundo\Model\Setup\ModelCollectionSetup;class WidgetInstall extends AbstractInstall{    public function install()    {        //(new ApplicationSetup())->addApplication(new WidgetApplication());        (new ModelCollectionSetup())->addCollection(new WidgetModelCollection());        (new ContentTypeSetup(new MeteoschweizApplication()))            ->addContentType(new MaxTemperatureTodayContentType())            ->addContentType(new StationSearchContentType());    }}