<?phpnamespace Nemundo\Meteoschweiz\Widget;use Nemundo\Admin\Com\Widget\AbstractAdminWidget;use Nemundo\Geo\Map\GeoAdmin\GeoAdminMap;use Nemundo\Geo\Map\GeoAdmin\GeoAdminMapLayer;use Nemundo\Meteoschweiz\Site\MesswertStationKmlSite;class MesswertStationMapWidget extends AbstractAdminWidget{    protected function loadWidget()    {       $this->widgetTitle='Messwert Station Map';    }    public function getContent()    {        $map = new GeoAdminMap($this);        $map->addLayer(GeoAdminMapLayer::PIXELKARTE);        $map->addKmlLayer(MesswertStationKmlSite::$site->getUrl());        return parent::getContent();    }}