<?phpnamespace Nemundo\Project\Deployment;use Nemundo\App\Application\Copy\AppPackageCopy;use Nemundo\Com\Package\PackageSetup;use Nemundo\Core\Base\AbstractBase;use Nemundo\Core\Debug\Debug;use Nemundo\Core\File\DirectoryCopy;use Nemundo\Core\File\DirectoryReader;use Nemundo\Core\File\FileCopy;use Nemundo\Core\Local\LocalCommand;use Nemundo\Core\Path\Path;use Nemundo\Core\Random\RandomNumber;use Nemundo\Core\TextFile\Writer\TextFileWriter;use Nemundo\Dev\Code\PhpFile;use Nemundo\Dev\Deployment\ProjectDeployment;use Nemundo\Dev\ProjectBuilder\Copy\AssetCopy;use Nemundo\Project\AbstractProject;use Nemundo\Project\Config\ConfigFileBuilder;abstract class AbstractDeployment extends AbstractBase{    /**     * @var AbstractProject     */    public $project;    protected $deploymentPath;    public $webPath = 'web';    /**     * @var bool     */    public $copySource = true;    /**     * @var bool     */    public $copyPackage = true;    /**     * @var bool     */    public $copyJsIndex = false;    public $copyPhpIndex = true;    public $phpIndexFilename = 'index.php';    public $jsIndexFilename;    public $htmlIndexFilename;    public $manifestFilename;    public $serviceWorkerFilename;    /**     * @var bool     */    protected $createConfigFile = true;    /**     * @var ConfigFileBuilder     */    protected $configFileBuilder = false;    /**     * @var bool     */    public $deleteBeforeDeploy = true;    abstract protected function loadDeployment();    public function __construct()    {        $this->configFileBuilder = new ConfigFileBuilder();        $this->loadDeployment();    }    public function getDeploymentPath()    {        $path = new Path($this->deploymentPath);        return $path;    }    // deployProject    public function deploy()    {        (new Path($this->deploymentPath))->createPath();        if ($this->deleteBeforeDeploy) {            //(new Path($this->deploymentPath))->emptyDirectory();            $reader = new DirectoryReader();            $reader->path = $this->deploymentPath;            $reader->includeDirectories = true;            $reader->includeFiles = true;            foreach ($reader->getData() as $file) {                if ($file->isDirectory()) {                    if (($file->getFilename() !== '.git') && ($file->getFilename() !== '.idea')) {                        (new Path($file->getFullFilename()))->deleteDirectory();                    }                }                if ($file->isFile()) {                    if ($file->getFilename() !== 'config.ini') {                        $file->deleteFile();                    }                }            }        }        $webPath = (new Path($this->deploymentPath))            ->addPath($this->webPath)            ->createPath()            ->getPath();        if ($this->copyPackage) {            $copy = new AppPackageCopy();            $copy->destinationPath = $webPath;            $copy->copyPackage();            foreach ($this->project->getApplicationList() as $application) {                foreach ($application->getPackageList() as $package) {                    $setup = new PackageSetup();                    $setup->destinationPath = $webPath;                    $setup->addPackage($package);                }            }        }        /** @var AbstractProject $project */        foreach ($this->project->getDependencyList() as $project) {            $path = (new Path('c:/dev/js/'))->addPath($project->projectName);            if ($path->existPath()) {                $copy = new DirectoryCopy();                //$copy->overwriteExistingFile=true;                $copy->sourcePath = $path->getPath();                $copy->destinationPath = (new Path($webPath))                    ->addPath('js')                    ->addPath($project->projectName)                    ->getPath();                $copy->copyDirectory();            }        }        /** @var AbstractProject $project */        foreach ($this->project->getDependencyList() as $project) {            $path = (new Path('c:/dev/css/'))->addPath($project->projectName);            if ($path->existPath()) {                $copy = new DirectoryCopy();                //$copy->overwriteExistingFile=true;                $copy->sourcePath = $path->getPath();                $copy->destinationPath = (new Path($webPath))                    ->addPath('css')                    ->addPath($project->projectName)                    ->getPath();                $copy->copyDirectory();            }        }        /*$copy = new DirectoryCopy();        //$copy->overwriteExistingFile=true;        $copy->sourcePath = 'd:/git/css/';        $copy->destinationPath = (new Path($webPath))            ->addPath('css')            ->getPath();        $copy->copyDirectory();*/        $composerList = [];        /** @var AbstractProject $project */        foreach ($this->project->getDependencyList() as $project) {            foreach ($project->getComposerList() as $composerLibrary) {                $composerList[] = $composerLibrary;            }        }        $composerList = array_unique($composerList);       /* (new Debug())->write($composerList);        exit;*/        $configFilename = (new Path($this->deploymentPath))            ->addPath('autoload.php')            ->getFullFilename();        $php = new PhpFile($configFilename);        $php->add('function autoloader($class) {');        $php->add('$class = str_replace(chr(92), DIRECTORY_SEPARATOR, $class);');        $php->add('include "src" . DIRECTORY_SEPARATOR . $class . ".php";');        $php->add('}');        $php->add('spl_autoload_register("autoloader");');        $php->saveFile();        $configFilename = (new Path($this->deploymentPath))            ->addPath('config.php')            ->getFullFilename();        $php = new PhpFile($configFilename);        $php->overwriteExistingFile = true;        if (sizeof($composerList) > 0) {            $php->add('require_once "vendor/autoload.php";');        }        $php->add('require_once "autoload.php";');        $php->add('\Nemundo\Project\ProjectConfig::$projectPath = __DIR__ . DIRECTORY_SEPARATOR;');        $php->add('(new \Nemundo\Project\Loader\MySqlProjectLoader())->loadProject();');        $php->add('\Nemundo\Web\WebConfig::$webPath = \Nemundo\Project\ProjectConfig::$projectPath . "' . $this->webPath . '" . DIRECTORY_SEPARATOR;');        $php->saveFile();        //if ($this->copyRedirect) {        if ($this->copyPhpIndex) {            //$index = new PhpFile($webPath . 'redirect.php');            $index = new PhpFile($webPath . $this->phpIndexFilename);            $index->add('ini_set("display_errors", 1);');            $index->add('error_reporting(E_ALL);');            $index->add('require "../config.php";');            $index->add('(new \\' . $this->project->webClass . '())->loadWeb();');            $index->saveFile();        }        if ($this->copyJsIndex) {            $copy = new FileCopy();            $copy->sourceFilename = $this->htmlIndexFilename;            $copy->destinationFilename = (new Path($webPath))                ->addPath('index.html')                ->getFullFilename();            $copy->copyFile();            $copy = new FileCopy();            $copy->sourceFilename = $this->jsIndexFilename;            $copy->destinationFilename = (new Path($webPath))                ->addPath('index.js')                ->getFullFilename();            $copy->copyFile();            $copy = new FileCopy();            $copy->sourceFilename = 'd:/git/icon/icon.png';            $copy->destinationFilename = (new Path($webPath))                ->addPath('icon')                ->addPath('icon.png')                ->getFullFilename();            $copy->copyFile();        }        if ($this->manifestFilename !== null) {            $copy = new FileCopy();            $copy->sourceFilename = $this->manifestFilename;            $copy->destinationFilename = (new Path($webPath))                ->addPath('manifest.json')                ->getFullFilename();            $copy->copyFile();        }        if ($this->serviceWorkerFilename !== null) {            $copy = new FileCopy();            $copy->sourceFilename = $this->serviceWorkerFilename;            $copy->destinationFilename = (new Path($webPath))                ->addPath('sw.js')                ->getFullFilename();            $copy->copyFile();        }        $index = new TextFileWriter($webPath . '.htaccess');        $index->addLine('RewriteEngine on');        $index->addLine('RewriteCond %{REQUEST_FILENAME} !-f');        $index->addLine('RewriteCond %{REQUEST_URI} !/$');        //$index->addLine('RewriteRule  ^(.*) redirect.php [L]');        $index->addLine('RewriteRule ^(.*) ' . $this->phpIndexFilename . ' [L]');        /*$index->addLine('php_value post_max_size 2000M');        $index->addLine('php_value upload_max_filesize 2000M');*/        $index->saveFile();        $setupFilename = (new Path($this->deploymentPath))            ->addPath('bin')            ->createPath()            ->addPath('setup.php')            ->getFullFilename();        $index = new PhpFile($setupFilename);        $index->add('require_once "config.php";');        $index->add('(new \\' . $this->project->setupClass . '())->run();');        $index->saveFile();        if ($this->copySource) {            foreach ($this->project->getDependencyList() as $project) {                $projectDeployment = new ProjectDeployment();                $projectDeployment->path = new Path($this->deploymentPath);                $projectDeployment->copyProject($project);            }        }        $copy = new AssetCopy();        $copy->destinationPath = $this->deploymentPath;        $copy            ->copyAsset('bin/config.php')            ->copyAsset('bin/clean.php')            ->copyAsset('bin/cmd.php')            ->copyAsset('bin/init.php')            ->copyAsset('commit.bat');        $filename = $this->getDeploymentPath()->addPath('.gitignore')->getFullFilename();        $file = new TextFileWriter($filename);        $file->addLine('/.idea');        $file->addLine('/config.ini');        $file->addLine('/log');        $file->addLine('/tmp');        $file->addLine('/data_redirect');        $file->addLine('/web/data');        $file->addLine('/web/tmp');        $file->addLine('/web/log');        $file->saveFile();        $filename = $this->getDeploymentPath()->addPath('deploy')->getFullFilename();        $file = new TextFileWriter($filename);        //$file->addLine('git config credential.helper store');        $file->addLine('git pull');        //$file->addLine('php bin/setup.php');        $file->addLine('/usr/local/php80/bin/php bin/setup.php');        /*$file->addLine('sudo chown -R www-data:www-data web/data');        $file->addLine('sudo chown -R www-data:www-data web/tmp');        $file->addLine('sudo chown -R www-data:www-data tmp');        $file->addLine('sudo chown -R www-data:www-data log');        $file->addLine('sudo chown -R www-data:www-data data_redirect');*/        $file->saveFile();        $filename = $this->getDeploymentPath()->addPath('install')->getFullFilename();        $file = new TextFileWriter($filename);        /** @var AbstractProject $project */        foreach ($this->project->getDependencyList() as $project) {            if ($project->isObjectOfClass(AbstractProject::class)) {                if ($project->hasSetupCommand()) {                    $file->addLine($project->getSetupCommand()->getCmdText());                }            }        }        $file->saveFile();        if ($this->createConfigFile) {            //$this->configFileBuilder->writeFile();        }        $filename = (new Path($this->deploymentPath))->addPath('start.bat')->getFullFilename();        $random = new RandomNumber();        $random->minNumber = 40000;        $random->maxNumber = 50000;        $port = $random->getNumber();        $file = new TextFileWriter($filename);        $file->addLine('start http://localhost:' . $port);        $file->addLine('cd ' . $this->webPath);        $file->addLine('php -S localhost:' . $port);        $file->saveFile();        //(new Debug())->write($composerList);        $cmd = new LocalCommand();        $cmd->showOutput = true;        $cmd->addCommand('cd /d ' . $this->deploymentPath);        foreach ($composerList as $composer) {            $cmd->addCommand('composer require ' . $composer);        }        $cmd->runCommand();        //exit;        $this->onDeploy();    }    protected function onDeploy()    {    }}