<?phpnamespace Nemundo\Project\Deployment\Git;use Nemundo\Project\Deployment\DeploymentConfig;abstract class AbstractGithubDeployment extends AbstractGitDeployment{    protected $githubUrl;    public function __construct()    {        $this->createConfigFile = false;        //$this->deleteBeforeDeploy = false;        parent::__construct();        $this->deploymentGitUrl = 'https://' . DeploymentConfig::$token . '@github.com/' . $this->githubUrl;        $this->deploymentPath = DeploymentConfig::$deploymentPath . $this->project->projectName;    }}