<?phpnamespace Nemundo\Roundshot\App\Container\Content\RoundshotContainer;use Nemundo\Package\Bootstrap\Form\BootstrapForm;use Nemundo\Roundshot\App\Container\Data\RoundshotContainerItem\RoundshotContainerItem;use Nemundo\Roundshot\Com\ListBox\RoundshotListBox;class AddRoundshotForm extends BootstrapForm{    public $containerId;    /**     * @var RoundshotListBox     */    private $roundshot;    public function getContent()    {        $this->roundshot=new RoundshotListBox($this);        $this->roundshot->validation=true;        return parent::getContent(); // TODO: Change the autogenerated stub    }    protected function onSubmit()    {        $data=new RoundshotContainerItem();        $data->ignoreIfExists=true;        $data->containerId=$this->containerId;        $data->itemId=$this->roundshot->getValue();        $data->save();    }}