<?phpnamespace Nemundo\Roundshot\App\Container\Content\RoundshotContainer;use Nemundo\Content\Form\AbstractContentForm;use Nemundo\Package\Bootstrap\FormElement\BootstrapTextBox;use Nemundo\Roundshot\App\Container\Data\RoundshotContainer\RoundshotContainerModel;use Nemundo\Roundshot\Com\ListBox\RoundshotListBox;class RoundshotContainerContentForm extends AbstractContentForm{    /**     * @var RoundshotContainerContentType     */    public $contentType;    /**     * @var BootstrapTextBox     */    private $container;    /**     * @var RoundshotListBox     */    //private $roundshot;    public function getContent()    {        //$this->roundshot        $model=new RoundshotContainerModel();        $this->container=new BootstrapTextBox($this);        $this->container->label=$model->container->label;        $this->container->validation=true;        return parent::getContent();    }    public function onSubmit()    {        $this->contentType->container=$this->container->getValue();        $this->contentType->saveType();    }}