<?phpnamespace Nemundo\Roundshot\App\Container\Content\RoundshotContainer;use Nemundo\Admin\Com\Table\AdminClickableTable;use Nemundo\Admin\Com\Table\Row\AdminClickableTableRow;use Nemundo\Content\View\AbstractContentView;use Nemundo\Roundshot\App\Container\Data\RoundshotContainerItem\RoundshotContainerItemReader;class RoundshotContainerContentView extends AbstractContentView{    /**     * @var RoundshotContainerContentType     */    public $contentType;    protected function loadView()    {        $this->viewName = 'default';        $this->viewId = 'bff717c6-bc4f-46ca-a6e1-e758ae6ef394';        $this->defaultView = true;    }    public function getContent()    {        $form = new AddRoundshotForm($this);        $form->containerId=$this->contentType->getDataId();        $table=new AdminClickableTable($this);        $reader = new RoundshotContainerItemReader();        $reader->model->loadItem();        $reader->filter->andEqual($reader->model->containerId,$this->contentType->getDataId());        foreach ($reader->getData() as $containerItemRow) {            $row=new AdminClickableTableRow($table);            $row->addText($containerItemRow->item->roundshot);            // delete by form        }        // Table        return parent::getContent();    }}