<?phpnamespace Nemundo\Roundshot\App\Container\Data\RoundshotContainer;class RoundshotContainerReader extends \Nemundo\Model\Reader\ModelDataReader {/*** @var RoundshotContainerModel*/public $model;public function __construct() {parent::__construct();$this->model = new RoundshotContainerModel();}/*** @return RoundshotContainerRow[]*/public function getData() {$list = [];foreach (parent::getData() as $dataRow) {$row = $this->getModelRow($dataRow);$list[] = $row;}return $list;}/*** @return RoundshotContainerRow*/public function getRow() {$dataRow = parent::getRow();$row = $this->getModelRow($dataRow);return $row;}/*** @return RoundshotContainerRow*/public function getRowById($id) {return parent::getRowById($id);}private function getModelRow($dataRow) {$row = new RoundshotContainerRow($dataRow, $this->model);$row->model = $this->model;return $row;}}